import React, { useState } from 'react';
import { diagnosisService } from '../services/api';
import './SymptomDiagnosis.css';

function SymptomDiagnosis() {
    const [symptoms, setSymptoms] = useState('');
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);

    const commonSymptoms = [
        'حمى', 'سعال', 'ألم الحلق', 'صداع', 'إرهاق',
        'ضيق التنفس', 'ألم الصدر', 'إسهال', 'غثيان', 'طفح جلدي'
    ];

    const handleAddSymptom = (symptom) => {
        const symptomList = symptoms ? symptoms.split(',') : [];
        if (!symptomList.map(s => s.trim()).includes(symptom)) {
            setSymptoms(symptoms ? `${symptoms}, ${symptom}` : symptom);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!symptoms.trim()) {
            alert('يرجى إدخال الأعراض');
            return;
        }

        setLoading(true);
        try {
            const symptomList = symptoms.split(',').map(s => s.trim());
            const response = await diagnosisService.diagnose({ symptoms: symptomList });
            setResult(response.data);
        } catch (err) {
            console.error(err);
            alert('خطأ في التشخيص');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="diagnosis-page">
            <div className="container">
                <h1 className="page-title">مشخص الأعراض الذكي</h1>
                <p className="subtitle">
                    ⚠️ تنبيه: هذا التطبيق لأغراض تعليمية فقط ولا يجب الاعتماد عليه بديلاً عن استشارة الطبيب
                </p>

                <div className="diagnosis-wrapper">
                    <form onSubmit={handleSubmit} className="diagnosis-form">
                        <h2>اختر أو أدخل الأعراض</h2>

                        <div className="symptoms-tags">
                            <p>الأعراض الشائعة:</p>
                            <div className="tags-list">
                                {commonSymptoms.map(symptom => (
                                    <button
                                        key={symptom}
                                        type="button"
                                        className="tag-btn"
                                        onClick={() => handleAddSymptom(symptom)}
                                    >
                                        {symptom}
                                    </button>
                                ))}
                            </div>
                        </div>

                        <div className="form-group">
                            <label htmlFor="symptoms">أدخل الأعراض (مفصولة بفاصلة)</label>
                            <textarea
                                id="symptoms"
                                value={symptoms}
                                onChange={(e) => setSymptoms(e.target.value)}
                                placeholder="مثال: حمى, سعال, إرهاق"
                                rows="4"
                            />
                        </div>

                        <button type="submit" className="btn" disabled={loading}>
                            {loading ? 'جاري التشخيص...' : 'تشخيص الآن'}
                        </button>
                    </form>

                    {result && (
                        <div className="diagnosis-result">
                            <h2>نتائج التشخيص</h2>
                            <div className="disclaimer-box">
                                {result.disclaimer}
                            </div>

                            <div className="conditions-list">
                                {result.possibleConditions && result.possibleConditions.length > 0 ? (
                                    result.possibleConditions.map((condition, index) => (
                                        <div key={index} className="condition-item">
                                            <div className="condition-header">
                                                <h3>{condition.name}</h3>
                                                <span className="match-percentage">
                                                    تطابق: {Math.round(condition.matchPercentage)}%
                                                </span>
                                            </div>
                                            <div className="match-bar">
                                                <div
                                                    className="match-fill"
                                                    style={{ width: `${condition.matchPercentage}%` }}
                                                ></div>
                                            </div>
                                            <div className={`severity severity-${condition.severity}`}>
                                                مستوى الخطورة: {condition.severity}/3
                                            </div>
                                            <p className="advice">{condition.advice}</p>
                                        </div>
                                    ))
                                ) : (
                                    <p>لم نتمكن من تحديد أي حالة محتملة</p>
                                )}
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default SymptomDiagnosis;
